mapper.update:
UPDATE t_banner b
SET b.FStatus = #{status},
 b.FUpdateTime = NOW(),
 b.FKUpdateBy = #{updateBy.id}
WHERE
    b.FID = #{id}

mapper.select:
SELECT b.FID id, b.FPart part, b.FTitle title, b.FPicture picture, b.FSummary summary, b.FID "info.id", b.FURL url, b.FWeight weight, b.FStatus status, b.FCreateTime createTime FROM t_banner b
b.FStatus = #{status}
AND b.FPart = #{part}
AND b.FTitle LIKE CONCAT('%', #{keyword}, '%')
ORDER BY b.FWeight DESC, b.FCreateTime DESC
